ObjectStates.LuaScript:
local generalLibeary={
    Adaptable={
        name="Adaptable";
        rules="Before performing a duel, this model may remove this token to add a suit of its choice to its duel total.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424848996/D551096CA632BAAFB6463163006306B238B08E6C/";
        color="#FFFFFF";
    },
    Adversary={
        name="Adversary";
        rules="Friendly models receive a + to attack actions targeting this model. During the end phase, remove this token.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554346517/81BCB3804E00F22B1E40D6A84C85C26F04F3C5CC/";
        color="#DF2020";
    },
    AuraConcealment={
        name="Aura(Concealment)";
        rules="The area within 2\" of this model is concealing terrain. Friendly models may choose to be unaffected by this terrain. During the end phase, remove this token.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424977137/1552D31690379EFEC945C0FF0CE85569BD74B3F2/";
        color="#FFFFFF";
    },
    AuraHazardous={
        name="Aura(Hazardous)";
        rules="The area within 2\" of this model is hazardous terrain. Friendly models may choose to be unaffected by this terrain. During the end phase, remove this token.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424981231/12D4FE1B392367DAAFBB49533FE4E651CF035480/";
        color="#FFFFFF";
    },
    Bolstered={
        name="Bolstered";
        rules="This model receives +1 to its Df and Wp. During the end phase, remove this token. Canceled by Injured.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992405/7D6A47E471482DC1AD39E943EEF1898FC5BA3B96/";
        color="#FFFFFF";
    },
    Burning={
        name="Burning";
        rules="At the end of the turn, deal 1 damage to this model and enemy models in base contact with it. Then remove this token";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554318009/A00031DC30FDC7D6EB7AEA3DFCF8AAD0754CD4CB/";
        color="#DB8E47";
    },
    Craven={
        name="Craven";
        rules="This model cannot declare the Interact action and is ignored for its crew’s strategies and schemes. At the end of this model’s activation, remove this token.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992470/3C9C3A7DADB2F3ADE3F65C78ABEC8C022A949FD7/";
        color="#FFFFFF";
    },
    Distracted={
        name="Distracted";
        rules="When this model targets an enemy model, it must remove this token and suffer a - to that action’s duel. Canceled by Focused.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554407310/DA85F94D429B073CEB18B2AB4F24FC0041F1CF30/";
        color="#FF42CF";
    },
    Entranced={
        name="Entranced";
        rules="This model’s actions that target an enemy model cannot be cheated. After this model resolves an action targeting an enemy model, remove this token.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992526/979B1FDFA5C5E66D65B9430C894EF7D1037CACDF/";
        color="#FFFFFF";
    },
    Fast={
        name="Fast";
        rules="Increase this model’s action limit by 1 (to a maximum of 3). When this model ends its activation, remove this token. Canceled by Slow.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554385209/698B13597E185E6ACA0AB19C13A118A3C1BEEB4D/";
        color="#E2D064";
    },
    Focused={
        name="Focused";
        rules="Before performing a duel, this model may remove this token to receive a + to the duel. Canceled by Distracted.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554725833/7B52C3AD5915BFC06B7E68025F5448C2862E0789/";
        color="#9A37D3";
    },
    Hastened={
        name="Hastened";
        rules="This model gains +2 Sp and cannot be moved by enemy models. When this model ends its activation, remove this token. Canceled by Staggered.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992579/9FAE4134ABE23FC7BF825B9A0CD465848309C298/";
        color="#FFFFFF";
    },
    Impact={
        name="Impact";
        rules="When this model succeeds in an attack action that deals damage, it must remove this token to deal +1 damage.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992619/F7CCDBFC67AC5A118A99785AAE8AD645ED9554DA/";
        color="#FFFFFF";
    },
    Injured={
        name="Injured";
        rules="This model suffers -1 to its Df and Wp. During the end phase, remove this token. Canceled by Bolstered.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554292969/5474B842DEC8F08CB249F3B24683FE95D58332FC/";
        color="#920606";
    },
    Insight={
        name="Insight";
        rules="Before performing a duel, this model may remove this token to look at the top card of its fate deck and may discard it.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992665/4FC08ADC9E2EE47293FA7EFE23CB9F0E812803B3/";
        color="#FFFFFF";
    },
    Poison={
        name="Poison";
        rules="At the end of the turn, deal 1 irreducible damage to this model.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554095214/37B3943D3C71EE6BFD13027145BDE00D0D56ED3B/";
        color="#83CD4D";
    },
    Shielded={
        name="Shielded";
        rules="When this model is dealt damage, it must remove this token to reduce that damage by 1. This token may reduce damage to 0.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554495459/45E41E4CF603049EEF4B3EAB39DC0B3DC93DFE78/";
        color="#6AC3FF";
    },
    Slow={
        name="Slow";
        rules="Reduce this model’s action limit by 1 (to a minimum of 1). When this model ends its activation, remove this token. Canceled by Fast.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554366701/F479B20690BB037348F53B802F99B9B68ACFCCEA/";
        color="#B8B8B8";
    },
    Staggered={
        name="Staggered";
        rules="This model suffers -2 Sp and cannot be moved by other enemy models. When this model ends its activation, remove this token. Canceled by Hastened.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554150868/60F4FB8B23A6586775CB50311E1F7DD6BC0C1620/";
        color="#138C01";
    },
    Stunned={
        name="Stunned";
        rules="This model cannot declare triggers and it counts all signature symbols on its card as blank. When this model ends its activation, remove this token.";
        image="https://steamusercontent-a.akamaihd.net/ugc/1019447087554191671/98EB8191C3884783E74F6FF8066097D2D7296CBF/";
        color="#FFFFFF";
    },
    Summon={
        name="Summon";
        rules="This model may not declare the Interact action. This model does not infuse a soutstones for its crew when it is killed. This token cannot be removed.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381424992707/BBCA04665101CE5DB9CF17ECE595CEB748011936/";
        color="#FFFFFF";
    },
}




local specialTokenLibeary={
    Flicker={
        name="Flicker";
        master="Tara";
        rules="Kill this model at the end of the turn. This token cannot be removed.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381425485377/47981C7F783EC204A9C2810E8E523C12AF28BC0D/";
        color="#FFFFFF";
    },
    Backtrack={
        name="Backtrack";
        master="Tara";
        rules="When performing a duel, this model may remove this token before any player cheats fate. If it does so, all cards in the conflict are discarded without effect and the duel is performed again with the same modifiers. This token may not be used if there are any jokers in the conflict.";
        image="https://steamusercontent-a.akamaihd.net/ugc/55840381425485306/6A397A40AAC29F6B113F57EC1B0B7E5678601579/";
        color="#FFFFFF";
    },
}


function getGenearyLibeary()
    return generalLibeary
end

function getSpecialLibeary()
    return specialTokenLibeary
end
