            b.drag=0
            b.use_snap_points=false
            b.use_grid=false
            b.use_gravity=false
            b.auto_raise=false
            b.auto_raise=false
            b.sticky=false
            b.interactable=false
        end
        })
        ChildObjs.aura_obj.setCustomObject({
