showAura()
end
function onSave()
end
function onUpdate()
end
------ STATE ACTIONS ------------------------
function SetInitialState(newState) --Tobe called from the reference card
end
function ModifyAura(params)
local bound = self.getBoundsNormalized();
state.conditions.Aura = math.max(0,state.conditions.Aura + params.amount);
