    return [[
        <Panel id=']]..color..[[_PlayerHUDPivot'  height="160" width="128" position='0 0 -10' rotation='0 0 0' rectAlignment="MiddleCenter"  childForceExpandWidth="false">
        ]]..HUDConditions(color) ..[[
        </Panel>
