    return [[<Panel id="]] .. id ..[[" width="30" height="30" alignment='LowerLeft' position=']] ..(x* 32 ).. [[ ]] .. y*(32) .. [[ 0' ]] .. 
        [[onClick='UI_Modify]] .. name ..[[()'>]] ..
        HUDSingleConditionBody(color,name)..
    [[</Panel>]];
end
